var MAX_NUM = 2147483647;
var state="start";	// start,gaming,over
var danmu_limit=24;	// 弹幕密度限制(难度)
var danmu_intervals=70;	// 弹幕发射间隔
var gameArea={x:0,y:0,w:430,h:568,padding:20};
var gameCanvas;
var mask;
var startMenu;
var gameMenu;
var gameTime;
var endMenu;
var scoreTxt;

var clock;
var _timer;
var ammo;
var danmu;
var userTv;

var danmaku_Speed=0.24;

function fillRect(g, x, y, width, height, color, alpha) 
{
	alpha = alpha || 1;
	g.graphics.beginFill(color,alpha);
	g.graphics.drawRect(x, y, width, height);
	g.graphics.endFill();
}

function createGameCanvas()
{
	gameCanvas = $.createCanvas({lifeTime:MAX_NUM, x:0, y:0});
	gameCanvas.transform.matrix3D = null;

	mask = $.createShape({x:0, y:0, lifeTime:0, parent:gameCanvas});
	fillRect(mask, 0, 0, gameArea.w, gameArea.h, 0x000000, 0.9);
	mask.transform.matrix3D = null;
	gameCanvas.mask = mask;

	var background = $.createShape({x:0, y:0, lifeTime:0, parent:gameCanvas});
	fillRect(background, 0, 0, gameArea.w, gameArea.h, 0x222024, 0.9);
	// fillRect(background, 0, 530, gameArea.w, 20, 0xf7ffde);
	// fillRect(background, 0, 640, gameArea.w, 645 - 640, 0x523942);
	// fillRect(background, 0, 645, gameArea.w, gameArea.h - 645, 0xdedf94);

	startMenu = $.createCanvas({x:0, y:0, lifeTime:0, parent:gameCanvas});
	$.createComment("发送含有“投币”内容的弹幕开始游戏\n\n方向键控制移动\nS键慢速模式\n\n更新:弹幕移动速度会随时间增加", {x:10, y:130, lifeTime:0, parent:startMenu, color:0xff0000, fontsize:22});
	$.createComment("\n\nnotes: 希望大家发送有意思的长弹幕来提升游戏难度", {x:10, y:430, lifeTime:0, parent:startMenu, color:0xff0000, fontsize:12});

	endMenu = $.createCanvas({x:0, y:0, lifeTime:0, parent:gameCanvas});
	scoreTxt = $.createComment("", {x:85, y:130, lifeTime:0, parent:endMenu, color:0xff0000, fontsize:26});
	endMenu.remove();

	gameMenu = $.createCanvas({x:0, y:0, lifeTime:0, parent:gameCanvas});
	gameTime = $.createComment("", {x:10, y:5, lifeTime:0, parent:gameMenu, color:0xffffff, fontsize:16});
	gameMenu.remove();

	gameCanvas.x = 215;
	gameCanvas.y = 0;
}



// 计时时钟
function createClock () {
	clock={};
	clock.ticCount=0;
	clock.timepast=function(msec){
		clock.ticCount+=msec;
	};
	clock.clear=function(){
		clock.ticCount=0;
	};
	clock.pop=function(){
		var output=clock.ticCount;
		clock.ticCount=0;
		return output;
	};
	// return clock;
	trace("定时模块载入");
}


function createTimer(){
	_timer={};
	_timer.ms=0;

	_timer.add=function(x){
		if(state=="gaming"){
			_timer.ms+=x;
			gameTime.text="存活时间: "+(_timer.ms/1000).toFixed(1)+" sec";
			// msg.remove();
			// msg=$.createComment("存活时间: "+_timer.ms/1000+" sec",{liftTime:0,x:200,y:0});
		}
	};

	_timer.get=function(){
		return _timer.ms;
	};
	_timer.clr=function(){
		_timer.ms=0;
	};
	// return _timer;
	trace("计时模块载入");
};


// 被B站doc坑了,明明是Player.commentList[n].text!!文档上面是txt!!害我半天才发现
// 弹药库
function createAmmo(){
	ammo={};
	ammo.count=Player.commentList.length;
	ammo.list=Player.commentList;
	// 弹药总量
	ammo.getTotal=function(){
		// return ammo.count;
		return Player.commentList.length;
	};
	// 取随机子弹
	ammo.randPick=function(){
		var max=Player.commentList.length;
		if(max>10 && Utils.rand(1,100)<85){
			return ammo.list[Utils.rand(1,max)].text;
		}else{
			return randList[Utils.rand(1,randList.length)];		// 随机字符串
		}
	};
	// return ammo;
	trace("弹幕装载模块载入");
}



function createDanmu(){
	danmu={};
	danmu.array=[];
	danmu.load=function(str){		// 装载弹幕字符串
		var _={};
		_.charArray=[];
		if(state=="gaming" && Utils.rand(1,100)<50 ){
			if(Utils.rand(1,50)<10) _.x=userTv.x+5;
			else _.x=userTv.x+Utils.rand(-100,100);
			if(_.x>gameArea.w) _.x-=100;
			if(_.x<gameArea.x) _.x+=100;
		}else{
			_.x=Utils.rand(gameArea.x-3,gameArea.w-10);
		}
		_.y=50;
		_.charArray=str.split("",30);	// 分隔并限制最大长度
		danmu.array.push(_);
		return danmu.array.length;	// 返回已载入的数量
	};

	danmu.deploy=function(){		// 部署弹幕字符串(the last one)
		var _=danmu.array[danmu.array.length-1];
		_.objArray=[];
		var y=50;
		var _easierIndex=0;
		var colord=Utils.hue(Utils.rand(1,359));
		for (var i = 0; i < _.charArray.length; i++) {

			if(i%7==0)
				_easierIndex+=2;		// 插入间距
			if(_.charArray[i]==" ")		// 替换空格
				_.charArray[i]="+";
			_.objArray.push({cmt:$.createComment(_.charArray[i],{x:_.x,y:y,lifeTime:0,color:colord,align:"center",parent:gameCanvas}),delay:300+(_easierIndex)*20.8/danmaku_Speed,
				collision:{
					x:4,y:4,w:10,h:10,	// offset
					obj:$.createShape({lifeTime:0,x:_.x,y:y,parent:gameCanvas})
				}
			});
			_easierIndex++;
			var _root=_.objArray[_.objArray.length-1];

			var collision=_root.collision;
			var g=collision.obj.graphics;

			collision.x=2;collision.y=2;
			collision.w=_root.cmt.width*0.7-6;
			collision.h=_root.cmt.height*0.7-4;

			// g.lineStyle(1,0xFF0000,0.5,true,"normal","round");	// 显示碰撞盒
			g.lineStyle(1,0xFF0000,0,true,"normal","round");	// 不显示碰撞盒
			g.moveTo(collision.x, collision.y);
			g.lineTo(collision.x, collision.h+collision.y);
			g.lineTo(collision.w+collision.x, collision.h+collision.y);
			g.lineTo(collision.w+collision.x, collision.y);
			g.lineTo(collision.x, collision.y);

			var format=_root.cmt.getTextFormat();
			// format.align="center";
			format.size=16;
			_root.cmt.setTextFormat(format);
			// trace(_.objArray[_.objArray.length-1].cmt.lifeTime);
		};
		return _.objArray.length;
	};

	var shakeInt=0;
	function shake(obj,x,y){
		if(shakeInt++<3) return;
		shakeInt=0;
		vx=10-20*Math.random();
		vy=5-10*Math.random();
		obj.x=x+vx;
		obj.y=y+vy;
	};

	danmu.collisionDetect=function(z,length,q){	// 子弹,移动距离,自机
		var inbox=function(a,box){
			if(a.x>box.x && a.x<box.x+box.w && a.y>box.y && a.y<box.y+box.h)
				return true;
			else
				return false;
		};
		var rect1={					// 子弹
			x:z.cmt.x+z.collision.x,
			y:z.cmt.y+z.collision.y,
			w:z.collision.w,
			h:z.collision.h+length
		};
		var rect2={					// 自机
			x:q.obj.x+q.collision.x,
			y:q.obj.y+q.collision.y,
			w:q.collision.w,
			h:q.collision.h
		};
		// 由于自机碰撞范围远远小于子弹，简化判断
		if( inbox(rect2,rect1) 
			|| inbox({x:rect2.x+rect2.w,y:rect2.y},rect1) 
			|| inbox({x:rect2.x+rect2.w,y:rect2.y+rect2.h},rect1) 
			|| inbox({x:rect2.x,y:rect2.y+rect2.h},rect1) 
			){
			// trace("DEAD");
			userTv.hit();
		}
	};

	danmu.update=function(dt){
		for (var i=0; i<danmu.array.length; i++) {
			var _=danmu.array[i];
			for(var j=0; j<_.objArray.length; j++){
				if(_.objArray[j].delay>0) {
					_.objArray[j].delay-=dt;
					if(_.objArray[j].delay>0)
						shake(_.objArray[j].cmt,_.x,_.y);
					else{
						_.objArray[j].cmt.x=_.x;
						_.objArray[j].cmt.y=_.y;
					}
				}
				else{
					if(userTv.show){
						danmu.collisionDetect(_.objArray[j],danmaku_Speed*dt,userTv);
					}
					_.objArray[j].cmt.y+=danmaku_Speed*dt;
					_.objArray[j].collision.obj.y=_.objArray[j].cmt.y;
					if(_.objArray[j].cmt.y>=gameArea.h){
						_.objArray[j].cmt.remove();
						_.objArray[j].collision.obj.remove();
					}
				}
			}
			if(_.objArray[_.objArray.length-1].cmt.y>=gameArea.h){
				danmu.array.splice(i,1);
				i-=1;
			}
		}
	};

	danmu.clear=function(){
		for (var i=0; i<danmu.array.length; i++) {
			var _=danmu.array[i];
			for(var j=0; j<_.objArray.length; j++){
				_.objArray[j].cmt.remove();
				_.objArray[j].collision.obj.remove();
			}
			danmu.array.splice(i,1);
			i-=1;
		}
	};
	// return danmu;
	trace("弹幕模块载入");
}


function createPlayer() {
	userTv={};
	// userTv.speed=0.25;
	userTv.x=-100;
	userTv.y=-100;
	userTv.show=false;

	userTv.obj=$.createShape({lifeTime:0,x:100,y:100,parent:gameCanvas});
	userTv.collision={
		x:14,y:11,w:4,h:4,
		// obj:$.createShape({lifeTime:0,x:userTv.x,y:userTv.y,parent:gameCanvas})
		obj:$.createCanvas({lifeTime:0,x:userTv.x,y:userTv.y,parent:gameCanvas})
	};

	userTv.Keysym = {
		65: 'A', 68: 'D', 83: 'S', 87: 'W',
		38:'UP',40:'DOWN',37:"LEFT",39:'RIGHT'
	};
	userTv.keyPressed = {};
	clearTimeout(Player.keyTrigger(function(keyCode) {
		userTv.keyPressed[userTv.Keysym[keyCode]] = true;
	}));
	clearTimeout(Player.keyTrigger(function(keyCode) {
		userTv.keyPressed[userTv.Keysym[keyCode]] = false;
	}, 1000, true));

	// $.root.addEventListener('mouseMove', function (e){
	// 	userTv.x=e.localX;
	// 	userTv.y=e.localY;
	// });

	about=function(x){return x+Utils.rand(-1,1);};
	userTv.collisionDot=$.createCanvas({lifeTime:0,x:0,y:0,parent:userTv.collision.obj});
	var _=$.createShape({lifeTime:0,x:0,y:0,parent:userTv.collisionDot});
	var g=_.graphics;
	g.beginFill(Utils.rgb(245,77,66));
	g.drawRect(userTv.collision.x,userTv.collision.y,userTv.collision.w,userTv.collision.h);
	g.endFill();
