		// 外框
		g.moveTo(about(0),about(0));
		g.lineTo(about(34),about(0));
		g.lineTo(about(34),about(24));
		g.lineTo(about(0),about(24));
		g.lineTo(about(0),about(0));
		// 内框
		g.moveTo(about(5),about(5));
		g.lineTo(about(30),about(5));
		g.lineTo(about(30),about(20));
		g.lineTo(about(5),about(20));
		g.lineTo(about(5),about(5));
		// 天线
		g.moveTo(about(7),about(-6));
		g.lineTo(about(15),about(0));
		g.moveTo(about(27),about(-9));
		g.lineTo(about(21),about(0));
		// 眼睛
		g.moveTo(about(8),about(11));
		g.lineTo(about(13),about(9));
		g.moveTo(about(21),about(8));
		g.lineTo(about(26),about(12));
		// 嘴
		g.moveTo(about(11),about(14));
		g.lineTo(about(14),about(18));
		g.lineTo(about(16),about(13));
		g.lineTo(about(20),about(18));
		g.lineTo(about(22),about(15));
		// 脚
		g.moveTo(about(4),about(24));
		g.lineTo(about(7),about(28));
		g.lineTo(about(10),about(24));
		g.moveTo(about(24),about(24));
		g.lineTo(about(27),about(28));
		g.lineTo(about(30),about(24));

		userTv.randTv[userTv.randTv.length-1].canvas.remove();
	};

	var drawInt=0;
	var drawIndex=0;
	drawTv=function(){	// 画小电视
		if(state!="gaming"){
			userTv.x=-100;
			userTv.y=-100;
		}
		userTv.obj.x=userTv.x;
		userTv.obj.y=userTv.y;

		if(drawInt++<5) return;		// 减少绘制次数 QAQ
			drawInt=0;

		userTv.randTv[drawIndex].canvas.remove();
		userTv.collisionDot.remove();
		drawIndex=Utils.rand(0,23);
		// userTv.randTv[drawIndex]
		userTv.collision.obj.addChild(userTv.randTv[drawIndex].canvas);
		if(userTv.keyPressed.S){
			userTv.collision.obj.addChild(userTv.collisionDot);
		}

		// userTv.obj.graphics.clear();

		// var g=userTv.obj.graphics;
		// g.lineStyle(2,Utils.rgb(77,77,78),1,true,"normal","round","round");

		// g.beginFill(Utils.rgb(245,245,245));
		// g.drawRect(0,0,34,24);	// bg
		// g.endFill();
		// // 外框
		// g.moveTo(about(0),about(0));
		// g.lineTo(about(34),about(0));
		// g.lineTo(about(34),about(24));
		// g.lineTo(about(0),about(24));
		// g.lineTo(about(0),about(0));
		// // 内框
		// g.moveTo(about(5),about(5));
		// g.lineTo(about(30),about(5));
		// g.lineTo(about(30),about(20));
		// g.lineTo(about(5),about(20));
		// g.lineTo(about(5),about(5));
		// // 天线
		// g.moveTo(about(7),about(-6));
		// g.lineTo(about(15),about(0));
		// g.moveTo(about(27),about(-9));
		// g.lineTo(about(21),about(0));
		// // 眼睛
		// g.moveTo(about(8),about(11));
		// g.lineTo(about(13),about(9));
		// g.moveTo(about(21),about(8));
		// g.lineTo(about(26),about(12));
		// // 嘴
		// g.moveTo(about(11),about(14));
		// g.lineTo(about(14),about(18));
		// g.lineTo(about(16),about(13));
		// g.lineTo(about(20),about(18));
		// g.lineTo(about(22),about(15));
		// // 脚
		// g.moveTo(about(4),about(24));
		// g.lineTo(about(7),about(28));
		// g.lineTo(about(10),about(24));
		// g.moveTo(about(24),about(24));
		// g.lineTo(about(27),about(28));
		// g.lineTo(about(30),about(24));
	};

	userTv.update=function(dt){
		var dx=0;
		var dy=0;
		var g=userTv.collision.obj.graphics;
		// g.clear();
		if(userTv.keyPressed.S){
			userTv.speed=0.13;
		}
		else
			userTv.speed=0.3;
		if(userTv.keyPressed.LEFT)
			dx=-userTv.speed*dt;
		else if(userTv.keyPressed.RIGHT)
			dx=userTv.speed*dt;
		if(userTv.keyPressed.UP)
			dy=-userTv.speed*dt;
		else if(userTv.keyPressed.DOWN)
			dy=userTv.speed*dt;
		if(dx!=0 && dy!=0){
			dx*=0.7;
			dy*=0.7;
		}
		userTv.move(dx,dy);
		drawTv();
	};

	userTv.move=function(dx,dy){
		userTv.x+=dx;
		userTv.y+=dy;
		if(userTv.x>gameArea.w-40) userTv.x=gameArea.w-40;
		if(userTv.y>gameArea.h-30) userTv.y=gameArea.h-30;
		if(userTv.x<gameArea.x) userTv.x=gameArea.x;
		if(userTv.y<gameArea.y+60) userTv.y=gameArea.y+60;
		userTv.collision.obj.x=userTv.x;
		userTv.collision.obj.y=userTv.y;
	};

	userTv.hit=function(){
		state="over";
		userTv.show=false;
		trace("阵亡");
		gameMenu.remove();
		gameCanvas.addChild(endMenu);
		scoreTxt.text="你已阵亡 QwQ\n存活时间:"+(_timer.get()/1000).toFixed(1)+"sec\n发送“投币”重新开始";
	};
	// return userTv;
	trace("自机模块载入");
}


var intervals=0;	// 装弹间隔
// 帧更新
function mainLoop(){
	if(state!="gaming" && state!="over" && Player.state=="playing"){
		Player.pause();
	}
	if(state=="gaming" && Player.state!="playing"){
		Player.play();
	}
	if(Player.time>=180000){
		Player.seek(20000);
	}
	intervals+=clock.pop();
	if(intervals>danmu_intervals){
		intervals=0;
		if(danmu.array.length<danmu_limit){
			danmu.load(ammo.randPick());
			// danmu.load("一i二i三!四!五六七八九");
			danmu.deploy();
		}
	}
	userTv.update(16);
	danmu.update(16);
}


function main()
{
	if(Global._get("game created"))
		return;

	$.frameRate = 60;
	ScriptManager.clearTimer();
	ScriptManager.clearEl();
	ScriptManager.clearTrigger();
	Player.seek(0);

	createGameCanvas();

	createClock();
	createTimer();
	createAmmo();
	createDanmu();
	createPlayer();

	interval(function(){clock.timepast(16);},16,0);
	interval(function(){_timer.add(100);if(state=="gaming"){danmaku_Speed+=0.0003;}},100,0);

	$.root.addEventListener("enterFrame", function(){$.root.setChildIndex(gameCanvas, $.root.numChildren-1);});	// 保持置顶
	Player.commentTrigger(function(cmt){
		if(cmt.text.search("投币")>=0 && (state=="start" || state=="over")){
			trace("检测到投币");
			state="gaming";
			danmu.clear();
			createAmmo();
			createDanmu();
			startMenu.remove();
			endMenu.remove();
			gameMenu.remove();
			gameCanvas.addChild(gameMenu);
			danmaku_Speed=0.24;
			userTv.x=100;
			userTv.y=500;
			userTv.show=true;
			_timer.clr();
			// $.createComment("第 "+Player.commentList.length+" 条弹幕",{liftTime:5,x:10,y:10});
			Player.seek(0);
			if(Player.state!="playing")
				Player.play();
			$.root.removeEventListener("enterFrame", mainLoop);
			$.root.addEventListener("enterFrame", mainLoop);
		}
	},MAX_NUM);
	Global._set("game created", true);
}

main();
